import React from "react";
import style from "./info.module.css";
import { v4 as uuidv4 } from "uuid";

function Info({summary, scan}) {
  return (
    <div className="container">
      <div className="row">
        <h1 className={style["title1"]}>סיכום מנהלים</h1>

        <div className={style["text80"]}>{scan}</div>
        <div className={style["memzaim65"]}>
          {summary && summary.length > 0 ? (
            <>
              <p>להלן הפירוט של הממצאים העיקריים:</p>
              {summary.map((item, index) => (
                <div
                  key={uuidv4()}
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}>
                  <span style={{ marginRight: "10px" }}>{index + 1} </span>
                  <span>{item}</span>
                </div>
              ))}
            </>
          ) : (
            <p>אין ממצאים להצגה</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Info;
