import React, { useEffect, useState } from "react";
import style from "./images.module.css";

function Images() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/api/")
      .then((response) => response.json())
      .then((data) => {
        setImages(data);
      })
      .catch((error) => {
        console.error("Error:", error);
        setImages([]);
      });
  }, []);

  if (!images || images.length === 0) {
    return <h2 className={style["title77"]}>לא נמצאו תמונות</h2>;
  }

  return (
    <div className="container">
      <div className="row">
        {images.map(
          (image, index) =>
            index < 9 && (
              <div className="col-md-4 mb-4">
                <img
                  src={image.url}
                  alt={image.alt || "Image"}
                  className={style["img-fluid image-style"]}
                />
              </div>
            )
        )}
        {images.length > 9 && (
          <div className="col-12">
            <a href="/" className={style["link790"]}>
              תמונות נוספות
            </a>
          </div>
        )}
      </div>
    </div>
  );
}

export default Images;
