import React, { useState, useEffect } from "react";
import style from "./nav.module.css";
import DownloadIcon from "@mui/icons-material/Download";
import Summary from "../summary/Summary";
import Business from "../business/Business";
import Data from "../data/Data";
import Status from "../status/Status";
import Online from "../online/Online";
import Networks from "../networks/Networks";
import MenuIcon from "@mui/icons-material/Menu";

/**
 *
 * A component responsible for the website's navigation menu, which adjusts according to mobile devices.
 *
 */

function Nav({ changeContent }) {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState("Summary");

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

const handleClick = (component, tabName) => {
  changeContent(component);
  setActiveTab(tabName); 
};


  return (
    <div className={style["nav"]}>
      <div className={style["menuIcon"]} onClick={toggleMenu}>
        <MenuIcon />
      </div>
      <nav className={isOpen ? `${style["active"]}` : ""}>
        <ul className={style["list3"]}>
          <li
          
            className={activeTab === "Business" ? style.active : ""}
            onClick={() => {
              changeContent(<Summary />);
              toggleMenu();
            }}>
            סיכום
          </li>
          <li
            className={activeTab === "Business" ? style.active : ""}
            onClick={() => {
              changeContent(<Business />);
              toggleMenu();
            }}>
            עסקי
          </li>
          <li
            onClick={() => {
              changeContent(<Data />);
              toggleMenu();
            }}>
            מאגרי מידע
          </li>
          <li
            onClick={() => {
              changeContent(<Status />);
              toggleMenu();
            }}>
            סטטוס משפטי
          </li>
          <li
            onClick={() => {
              changeContent(<Online />);
              toggleMenu();
            }}>
            אזכורים ברשת
          </li>
          <li
            onClick={() => {
              changeContent(<Networks />);
              toggleMenu();
            }}
            className={style.last}>
            רשתות חברתיות
          </li>
          <li className={style.last556}>
            <DownloadIcon />
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Nav;
