import React, { useState, useEffect } from "react";
import style from "./summary.module.css";
import Images from "../images/Images";
import Info from "../info/Info";
import Person from "../person/Person";
function Summary() {
  const [data, setData] = useState({});

  useEffect(() => {
    fetch("http://localhost:8000/api/data")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  return (
    <div className={`container ${style["size78"]}`}>
      <div className="row">
        <div className={`col-md-3 ${style["card-like"]}`}>
          <Person />
        </div>
        <div className={`col-md-5 ${style["card-like"]} ${style["size4"]}`}>
          <Info summary={data.summary} />
        </div>
        <div className={`col-md-3 ${style["card-like"]}  ${style["size2"]}`}>
          <Images images={data.secondaryImages} />
        </div>
      </div>
    </div>
  );
}
export default Summary;
