import React from "react";
import style from "./info.module.css";
import { v4 as uuidv4 } from "uuid";




function Info({ summary, scan }) {
  return (
    <div className="container">
      <div className="row">
        <h1 className={style["title1"]}>סיכום מנהלים</h1>

        <div className={style["text80"]}>{scan}</div>
        <p className={style["memzaim65"]}>להלן הפירוט של הממצאים העיקריים:</p>
        <div className={style["container109"]}>
          {summary && summary.length > 0 ? (
            <>
              {summary.map((item, index) => (
                <div
                  key={uuidv4()}
                  style={{
                    display: "flex",
                    fontSize: "16px",
                    color: "#233664",
                    fontWeight: "500",
                    backgroundColor: "#F4F9FB",
                    paddingTop: "20px",
                  }}>
                  <span
                    style={{
                      marginLeft: "10px",
                      paddingRight: "30px",
                      marginBottom: "23px",
                      fontSize: "22px",
                      color: "#00BEC6",
                    }}>
                    {index + 1}
                  </span>
                  <span
                    style={{
                      marginBottom: "10px",
                      width: "83%",
                    }}>
                    {item}
                  </span>
                </div>
              ))}
            </>
          ) : (
            <p>אין ממצאים להצגה</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Info;
