import React, { useState, useEffect } from "react";
import style from "./nav.module.css";
import DownloadIcon from "@mui/icons-material/Download";
import Summary from "../summary/Summary";
import Business from "../business/Business";
import Data from "../data/Data";
import Status from "../status/Status";
import Online from "../online/Online";
import Networks from "../networks/Networks";
import MenuIcon from "@mui/icons-material/Menu";

/**
 *
 * A component responsible for the website's navigation menu, which adjusts according to mobile devices.
 *
 */

function Nav({ changeContent }) {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState("Summary");

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleClick = (component, tabName) => {
    changeContent(component);
    setActiveTab(tabName);
  };

  return (
    <div className={style["nav"]}>
      <div className={style["menuIcon"]} onClick={toggleMenu}>
        <MenuIcon />
      </div>
      <nav className={isOpen ? `${style["active"]}` : ""}>
        <ul className={style["list3"]}>
          <li
            className={activeTab === "Summary" ? style.active : ""}
            onClick={() => {
              changeContent(<Summary />);
              toggleMenu();
              handleClick(<Summary />, "Summary");
            }}>
            סיכום
          </li>
          <li
            className={activeTab === "Business" ? style.active : ""}
            onClick={() => {
              changeContent(<Business />);
              toggleMenu();
              handleClick(<Business />, "Business");
            }}>
            עסקי
          </li>
          <li
            className={activeTab === "Data" ? style.active : ""}
            onClick={() => {
              changeContent(<Data />);
              toggleMenu();
              handleClick(<Data />, "Data");
            }}>
            מאגרי מידע
          </li>
          <li
            className={activeTab === "Status" ? style.active : ""}
            onClick={() => {
              changeContent(<Status />);
              toggleMenu();
              handleClick(<Status />, "Status");
            }}>
            סטטוס משפטי
          </li>
          <li
            className={activeTab === "Online" ? style.active : ""}
            onClick={() => {
              changeContent(<Online />);
              toggleMenu();
              handleClick(<Online />, "Online");
            }}>
            אזכורים ברשת
          </li>
          <li
            className={activeTab === "Networks last" ? style.active : "last"}
            onClick={() => {
              changeContent(<Networks />);
              toggleMenu();
              handleClick(<Networks />, "Networks");
            }}>
            רשתות חברתיות
          </li>
          <li className={style.last556}>
            <DownloadIcon />
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Nav;
