import React, { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import style from "./images.module.css";

function Images({ images }) {
  if (!images || images.length === 0) {
    return <h2 className={style["title77"]}>לא נמצאו תמונות</h2>;
  }

  return (
    <div className="container">
      <div className={`row ${style["space678"]}`}>
        <h2 className={style["title77"]}>תמונות ווידאו שנמצאו</h2>

        {images.map(
          (image, index) =>
            index < 9 && (
              <div
                key={image.id || index}
                className={`col-md-4 ${style["containerimg8"]}`}>
                <img
                  src={image.url}
                  alt={image.alt || "Image"}
                  className={style["image-style"]}
                />
              </div>
            )
        )}

        {images.length > 9 && (
          <div className={`col-md-12 ${style["containerimg8"]}`}>
            <a href="/" className={style["link790"]}>
              תמונות נוספות
            </a>
          </div>
        )}
      </div>
    </div>
  );
}

export default Images;
