import React, { useState, useEffect } from "react";
import style from "./media.module.css";

/**
 * 
 * A component that receives the media from the JSON and returns the media. If there are more than 9 media files, it adds the notation 'additional media' to access the page or load more from the media.
 */

function Media({ media }) {
  if (!media || media.length === 0) {
    return <h2 className={style["title5"]}>לא נמצאו תמונות</h2>;
  }

  return (
    <div className="container">
      <div className={`row ${style["space678"]}`}>
        <h2 className={style["title77"]}>תמונות ווידאו שנמצאו</h2>

        {media.map(
          (media, index) =>
            index < 9 && (
              <div
                key={media.id || index}
                className={`col-md-4 ${style["containerimg8"]}`}>
                <img
                  src={media.url}
                  alt={media.alt || "Image"}
                  className={style["image-style"]}
                />
              </div>
            )
        )}

        {media.length > 9 && (
          <div className={`col-md-12 ${style["containernew"]}`}>
            <a href="/" className={style["link790"]}>
              תמונות נוספות
            </a>
          </div>
        )}
      </div>
    </div>
  );
}

export default Media;
