import React, { useState, useEffect } from "react";
import style from "./nav.module.css";
import DownloadIcon from "@mui/icons-material/Download";
import Summary from "../summary/Summary";
import Business from "../business/Business";
import Data from "../data/Data";
import Status from "../status/Status";
import Online from "../online/Online";
import Networks from "../networks/Networks";
import MenuIcon from "@mui/icons-material/Menu";

function Nav({ changeContent }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={style.nav}>
      <div className={style.menuIcon} onClick={toggleMenu}>
        <MenuIcon /> 
      </div>
      <nav className={isOpen ? `${style.active}` : ""}>
        <ul className={style.list3}>
          <li
            onClick={() => {
              changeContent(<Summary />);
              toggleMenu();
            }}>
            סיכום
          </li>
          <li
            onClick={() => {
              changeContent(<Business />);
              toggleMenu();
            }}>
            עסקי
          </li>
          <li
            onClick={() => {
              changeContent(<Data />);
              toggleMenu();
            }}>
            מאגרי מידע
          </li>
          <li
            onClick={() => {
              changeContent(<Status />);
              toggleMenu();
            }}>
            סטטוס משפטי
          </li>
          <li
            onClick={() => {
              changeContent(<Online />);
              toggleMenu();
            }}>
            אזכורים ברשת
          </li>
          <li
            onClick={() => {
              changeContent(<Networks />);
              toggleMenu();
            }}
            className={style.last}>
            רשתות חברתיות
          </li>
          <li className={style.last556}>
            <DownloadIcon />
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Nav;
