import React, { useState, useEffect } from "react";
import style from "./summary.module.css";
import Media from "../media/Media";
import Info from "../info/Info";
import Person from "../person/Person";
import Reports from "../reports/Reports";
import Spinner from "../spinner/Spinner";
/**
 * 
 * A component that displays the summary section and receives data from the JSON file, then sends the data to the appropriate components.
 * @returns the summary section.
 */

function Summary() {
  const [data, setData] = useState({});
  const [isLoading, setIsLoading] = useState(false); 


  useEffect(() => {
    setIsLoading(true);
    fetch("http://localhost:8000/api/data")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("Error:", error);
        setIsLoading(false);
      });
  }, []);

  return (
    
    <div className={`container ${style["size78"]}`}>
      <div className="row">
        <div className={`col-md-3 ${style["card-like"]} ${style["size645"]}`}>
          <Person {...data} />
        </div>
        <div className={`col-md-6 ${style["card-like"]} ${style["size4"]}`}>
          <Info summary={data.summary} scan={data.scan} />
        </div>
        <div className={`col-md-2 ${style["card-like"]}  ${style["size2"]}`}>
          <Media media={data.secondaryImages} />
        </div>
      </div>
      <div className={`col-md-12 ${style["card-like"]}  ${style["size67"]}`}>
        <Reports />
      </div>
    </div>
  );
}
export default Summary;
